        brand: [],
        search: ""
    });

    const [productsList, setProductsList] = useState(products);

    // FILTER PRODUCTS BY SIZE
    const filterBySize = (size) => {
        setFilter({
            ...filter,
            size: size
        });

        const filteredProducts = products.filter(product => {
            return product.size.includes(size);
        });

        setProductsList(filteredProducts);
    }

    // FILTER PRODUCTS BY TYPE
    const filterByType = (type) => {
        setFilter({
            ...filter,
            type: type
        });

        const filteredProducts = productsList.filter(product => {
            return product.type === Number(type);
        });
        
        setProductsList(filteredProducts);
    }

    // FILTER PRODUCTS BY YEAR
    const filterByYear = (year) => {
        setFilter({
            ...filter,
            year: year
        });

        const filteredProducts = products.filter(product => {
            return product.date === year;
        });

        setProductsList(filteredProducts);
    }

    // FILTER PRODUCTS BY BRAND
    const filterByBrand = (brand) => {
        setFilter({
            ...filter,
            brand: brand
        });

        const filteredProducts = products.filter(product => {
            return product.brand === brand;
        });

        setProductsList(filteredProducts);

    }

    // FILTER PRODUCTS BY SEARCH
    const filterBySearch = (search) => {
        setFilter({
            ...filter,
            search: search
        });

        const filteredProducts = products.filter(product => {
            return product.name.toLowerCase().includes(search.toLowerCase());
        });

        setProductsList(filteredProducts);
    }

    // FILTER PRODUCTS BY ALL
    const filterByAll = () => { 
        if(filter.size.length > 0 || filter.type.length > 0 || filter.year.length > 0 || filter.brand.length > 0 || filter.search.length > 0) {
            const filteredProducts = products.filter(product => {
                return product.size.includes(filter.size) && product.type === filter.type && product.date === filter.year && product.brand === filter.brand && product.name.toLowerCase().includes(filter.search.toLowerCase());
            });

            setProductsList(products);
        } else {
            setProductsList(products);
        }
    }


    return (
        <div class="bg-white">
            <main class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div class="relative z-10 flex items-baseline justify-between pt-24 pb-6 border-b border-gray-200">
                    <h1 class="text-4xl font-extrabold tracking-tight text-gray-900">
                        New Arrivals
                    </h1>

                    <div
                        class="flex items-center relative"
                        id="tag-container"
                    ></div>
                </div>

                <section aria-labelledby="products-heading" class="pt-6 pb-24">

                    <div class="grid grid-cols-1 lg:grid-cols-4 gap-x-8 gap-y-10">
                        <form class="hidden lg:block">
                            <h3 class="py-3 bg-white w-full flex items-center justify-between text-sm text-gray-400 hover:text-gray-500">
                                Type</h3>

                            <div class="flex justify-center border-b border-gray-200 pb-6">
                                <div class="mb-3 xl:w-96">
                                    <select onChange={ (event) => {filterByType(event.target.value)}}
                                        class="form-select appearance-none block w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding bg-no-repeat border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                                        aria-label="Default select example" name="type" id="type-select">
                                        <option defaultValue={0}>
                                            Open this select menu
                                        </option>
                                        {
                                            Object.keys(types).map(key => {
                                                return <option value={key}>{types[key]}</option>
                                            })
                                        }
                                    </select>
                                </div>
                            </div>

                            <h3 class="py-3 bg-white w-full flex items-center justify-between text-sm text-gray-400 hover:text-gray-500">
                                Size</h3>

                            <div class="flex justify-center border-b border-gray-200 pb-6">
                                <div class="mb-3 xl:w-96">
                                    <select
                                        class="form-select appearance-none block w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding bg-no-repeat border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                                        aria-label="Default select example">
                                        <option selected>
                                            Open this select menu
                                        </option>
                                        {
                                            Object.keys(sizes).map(key => {
                                                return <option value={key}>{sizes[key]}</option>
                                            })
                                        }
                                    </select>
                                </div>
                            </div>

                            <h3 class="py-3 bg-white w-full flex items-center justify-between text-sm text-gray-400 hover:text-gray-500">
                                Years</h3>

                            <div class="flex justify-center border-b border-gray-200 pb-6">
                                <div class="mb-3 xl:w-96">
                                    <select
                                        class="form-select appearance-none block w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding bg-no-repeat border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                                        aria-label="Default select example">
                                        <option selected>
                                            Open this select menu
                                        </option>
                                        {
                                            Object.keys(years).map(key => {
                                                return <option value={key}>{years[key]}</option>
                                            })
                                        }
                                    </select>
                                </div>
                            </div>

                            <h3 class="py-3 bg-white w-full flex items-center justify-between text-sm text-gray-400 hover:text-gray-500">
                                Years</h3>

                            <div class="flex justify-center border-b border-gray-200 pb-6">
                                <div class="mb-3 xl:w-96">
                                    <select
                                        class="form-select appearance-none block w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding bg-no-repeat border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                                        aria-label="Default select example">
                                        <option selected>
                                            Open this select menu
                                        </option>
                                        {
                                            Object.keys(years).map(key => {
                                                return <option value={key}>{years[key]}</option>
                                            })
                                        }
                                    </select>
                                </div>
                            </div>

                            <h3 class="py-3 bg-white w-full flex items-center justify-between text-sm text-gray-400 hover:text-gray-500">
                                Brands</h3>

                            <div class="flex justify-center border-b border-gray-200 pb-6">
                                <div class="mb-3 xl:w-96">
                                    <select
                                        class="form-select appearance-none block w-full px-3 py-1.5 text-base font-normal text-gray-700 bg-white bg-clip-padding bg-no-repeat border border-solid border-gray-300 rounded transition ease-in-out m-0 focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none"
                                        aria-label="Default select example">
                                        <option selected>
                                            Open this select menu
                                        </option>
                                        {
                                            Object.keys(brands).map(key => {
                                                return <option value={key}>{brands[key]}</option>
                                            })
                                        }
                                    </select>
                                </div>
                            </div>

                        </form>

                        <div class="grid grid-cols-3 gap-4 lg:col-span-3">
                            {
                                Object.keys(products).map(key => {
                                    return <Item product={products[key]} />
                                })
                            }
                        </div>
                    </div>
                </section>
            </main>
        </div>
    );
};

export default ProductsList;
