import SignIn from "./SignIn";
import { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import {Link} from "react-router-dom";
import DB_PRODUCTS from "../database/products_db";
import DB_ATTRIBUTES from "../database/attributes_db";

const Cart = () => {
    const [token, setToken] = useState(localStorage.getItem("token"));
    const [cartList, setCartList] = useState(localStorage.getItem("cartList") ? JSON.parse(localStorage.getItem("cartList")) : []);
    const [totalPrice, setTotalPrice] = useState(0);

    const calculateTotalPrice = () => {
        let total = 0;
        cartList.map(cart => {
            total += DB_PRODUCTS.filter(product => product.id === cart.product_id)[0].price * cart.quantity;
        });
        return total;
    };

    const addShippingCharge = (value) => {
        setTotalPrice(calculateTotalPrice() + value);
        return total;
    };

    useEffect(() => {
        setTotalPrice(addShippingCharge());
    }, [totalPrice, cartList]);


    if (!token) {
        return <SignIn setToken={setToken} />
    }

    return (
        <div class="container mx-auto mt-10">
            <div class="flex shadow-md my-10">
                <div class="w-3/4 bg-white px-10 py-10">
                    <div class="flex justify-between border-b pb-8">
                        <h1 class="font-semibold text-2xl">Shopping Cart</h1>
                        <h2 class="font-semibold text-2xl">3 Items</h2>
                    </div>
                    <div class="flex mt-10 mb-5">
                        <h3 class="font-semibold text-gray-600 text-xs uppercase w-2/5">Product Details</h3>
                        <h3 class="font-semibold text-center text-gray-600 text-xs uppercase w-1/5 text-center">Quantity</h3>
                        <h3 class="font-semibold text-center text-gray-600 text-xs uppercase w-1/5 text-center">Price</h3>
                        <h3 class="font-semibold text-center text-gray-600 text-xs uppercase w-1/5 text-center">Total</h3>
                    </div>
                    {
                        cartList.map((item, index) => {
                            const product = DB_PRODUCTS.filter(product => product.id === item.product_id)[0];

                            return (
                                <div class="flex items-center hover:bg-gray-100 -mx-8 px-6 py-5">
                                    <div class="flex w-2/5"> 
                                        <div class="w-20">
                                            <img class="h-24" src={product.image} alt=""/>
                                        </div>
                                        <div class="flex flex-col justify-between ml-4 flex-grow">
                                            <span class="font-bold text-sm">{product.name}</span>
                                            <span class="text-red-500 text-xs">
                                                {DB_ATTRIBUTES.brands.filter((attribute) => Number(attribute.uid) === Number(product.brand))[0].value}
                                            </span>
                                            <a href="#" class="font-semibold hover:text-red-500 text-gray-500 text-xs">Remove</a>
                                        </div>
                                    </div>
                                    <div class="flex justify-center w-1/5">
                                        <input class="mx-2 border text-center w-14" type="number" value={item.quantity}/>
                                    </div>
                                    <span class="text-center w-1/5 font-semibold text-sm">BDT. {product.price}</span>
                                    <span class="text-center w-1/5 font-semibold text-sm">BDT. {product.price * item.quantity}</span>
                                </div>
                            )
                        })
                    }
                    

                    <Link to="/" class="flex font-semibold text-indigo-600 text-sm mt-10">

                        <svg class="fill-current mr-2 text-indigo-600 w-4" viewBox="0 0 448 512"><path d="M134.059 296H436c6.627 0 12-5.373 12-12v-56c0-6.627-5.373-12-12-12H134.059v-46.059c0-21.382-25.851-32.09-40.971-16.971L7.029 239.029c-9.373 9.373-9.373 24.569 0 33.941l86.059 86.059c15.119 15.119 40.971 4.411 40.971-16.971V296z" /></svg>
                        Continue Shopping
                    </Link>
                </div>

                <div id="summary" class="w-1/4 px-8 py-10">
                    <h1 class="font-semibold text-2xl border-b pb-8">Order Summary</h1>
                    <div class="flex justify-between mt-10 mb-5">
                        <span class="font-semibold text-sm uppercase">Items {cartList.length}</span>
                        <span class="font-semibold text-sm">
                            BDT. { calculateTotalPrice() }
                        </span>
                    </div>
                    <div>
                        <label class="font-medium inline-block mb-3 text-sm uppercase">Shipping</label>
                        <select class="block p-2 text-gray-600 w-full text-sm" onChange={(event) => addShippingCharge(event.target.value)}>
                            <option value={0}>Cash On Delivery</option>
                            <option value={60}>Inside Dhaka</option>
                            <option value={150}>Outside Dhaka</option>
                        </select>
                    </div>
                    <div class="border-t mt-8">
                        <div class="flex font-semibold justify-between py-6 text-sm uppercase">
                            <span>Total cost</span>
                            <span>BDT. { calculateTotalPrice() }</span>
                        </div>
                        <button class="bg-indigo-500 font-semibold hover:bg-indigo-600 py-3 text-sm text-white uppercase w-full">Checkout</button>
                    </div>
                </div>

            </div>
        </div>
    );
};

export default Cart;
