      type: 0,
    },
  ];

  const [filterSize, setFilterSize] = useState("");
  const [filterType, setFilterType] = useState("");
  const [filterYear, setFilterYear] = useState("");
  const [filterBrand, setFilterBrand] = useState("");
  const [filterPrice, setFilterPrice] = useState("");
  const [filterSearch, setFilterSearch] = useState("");
  const [filter, setFilter] = useState([]);

  const [productsList, setProductsList] = useState(products);

  // FILTER PRODUCTS BY SIZE
  let filterBySize = (size) => {
    setFilterSize(size);
    setFilter([...filter, size]);

    const filteredProducts = products.filter((product) => {
      return product.size.includes(size);
    });

    setProductsList(filteredProducts);
  };

  // FILTER PRODUCTS BY TYPE
  let filterByType = (type) => {
    setFilterType(type);
    setFilter([...filter, type]);
    console.log(filter);

    const filteredProducts = productsList.filter((product) => {
      return product.type === Number(type);
    });

    setProductsList(filteredProducts);
  };

  // FILTER PRODUCTS BY YEAR
  let filterByYear = (year) => {
    setFilterYear(year);
    setFilter([...filter, year]);

    const filteredProducts = products.filter((product) => {
      return product.date === year;
    });

    setProductsList(filteredProducts);
  };

  // FILTER PRODUCTS BY BRAND
  let filterByBrand = (brand) => {
    setFilterBrand(brand);
    setFilter([...filter, brand]);

    const filteredProducts = products.filter((product) => {
      return product.brand === brand;
    });

    setProductsList(filteredProducts);
  };

  // FILTER PRODUCTS BY SEARCH
  let filterBySearch = (search) => {
    setFilterSearch(search);
    setFilter([...filter, search]);

    const filteredProducts = products.filter((product) => {
      return (
        product.name.toLowerCase().includes(search.toLowerCase()) ||
        product.brand.toLowerCase().includes(search.toLowerCase()) ||
        product.category.toLowerCase().includes(search.toLowerCase())
      );
    });

    setProductsList(filteredProducts);
  };

  // FILTER PRODUCTS BY ALL
  let filterByAll = () => {
    if (
      filterBrand !== null &&
      filterSize !== null &&
      filterType !== null &&
      filterYear !== null
    ) {
      const filteredProducts = products.filter((product) => {
        return (
          product.brand === filterBrand &&
          product.size.includes(filterSize) &&
          product.type === Number(filterType) &&
          product.date === filterYear
        );
      });

      setProductsList(filteredProducts);
    }
  };

  const filterByCategory = (category) => {
    // setFilterCategory(category);
    setFilter([...filter, category]);

    const filteredProducts = products.filter((product) => {
      return product.category === category;
    });

    setProductsList(filteredProducts);
  };

  const filterByPrice = (min, max) => {
    setFilterPrice(min + " - " + max);
    setFilter([...filter, min + " - " + max]);

    const filteredProducts = products.filter((product) => {
      return product.cost >= min && product.cost <= max;
    });

    setProductsList(filteredProducts);
  };

  const handleRemoveTag = (tag) => { };

  return (
    <div className="App">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Layout />}>
            <Route index element={<Home />} />
            <Route path="categories" element={<Categories />} />
            <Route
              path="login"
              element={<AuthenticationSignIn />}
            />
            <Route
              path="products-list"
              element={<ProductsList products={productsList} handleRemoveTag={handleRemoveTag}
                handleFilterType={filterByType} handleFilterBrand={filterByBrand} handleFilterCategory={filterByCategory} handleSearch={filterBySearch}
                handleFilterSize={filterBySize} handleFilterYear={filterByYear} handleFilterPrice={filterByPrice} handleFilterAll={filterByAll} types={types} sizes={sizes}
                years={years} brands={brands} filter={filter}/>}
            />
          </Route>
        </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
