import DB_PRODUCTS from "../database/products_db";
import { useState } from "react";
import DB_ATTRIBUTES from "../database/attributes_db";
import { Link } from "react-router-dom";

const Home = () => {
    const [products, setProducts] = useState(DB_PRODUCTS);
    const [attributes, setAttributes] = useState(DB_ATTRIBUTES);

    return (
        <div className="bg-white">
            <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
                <h2 className="text-2xl font-extrabold tracking-tight text-gray-900">Our Categories</h2>

                <div className="mt-6 grid grid-cols-1 gap-y-10 gap-x-10 sm:grid-cols-2 lg:grid-cols-5 xl:gap-x-10">
                    {
                        attributes.category.map((cat, index) => {
                            return (
                                <div className="group relative">
                                    <div className="relative w-full h-80 bg-white rounded-lg overflow-hidden group-hover:opacity-75 sm:aspect-w-2 sm:aspect-h-1 sm:h-64 lg:aspect-w-1 lg:aspect-h-1">
                                        <img src={ cat.image } 
                                            alt="Desk with leather desk pad, walnut desk organizer, wireless keyboard and mouse, and porcelain mug." 
                                            className="w-full h-full object-center object-cover" />
                                    </div>
                                    <h3 className="mt-6 text-sm text-gray-500">
                                        <p>
                                            <span className="absolute inset-0"></span>
                                            { cat.value }
                                        </p>
                                    </h3>
                                    <p className="text-base font-semibold text-gray-900">
                                        {
                                            products.filter(item => item.category === cat.uid ).length
                                        }
                                    </p>
                                </div>
                            )
                        })
                    }
                </div>
            </div>

            <div className="max-w-2xl mx-auto py-16 px-4 sm:py-24 sm:px-6 lg:max-w-7xl lg:px-8">
                <h2 className="text-2xl font-extrabold tracking-tight text-gray-900">Our Categories</h2>

                <div className="mt-6 grid grid-cols-1 gap-y-10 gap-x-10 sm:grid-cols-2 lg:grid-cols-5 xl:gap-x-10">
                    {
                        products.map((cat, index) => {
                            return (
                                <div className="group relative">
                                    <div className="relative w-full h-80 bg-white rounded-lg overflow-hidden group-hover:opacity-75 sm:aspect-w-2 sm:aspect-h-1 sm:h-64 lg:aspect-w-1 lg:aspect-h-1">
                                        <img src={ cat.image } 
                                            alt="Desk with leather desk pad, walnut desk organizer, wireless keyboard and mouse, and porcelain mug." 
                                            className="w-full h-full object-center object-cover" />
                                    </div>
                                    <h3 className="mt-6 text-sm text-gray-500">
                                        <p>
                                            <span className="absolute inset-0"></span>
                                            { cat.name }
                                        </p>
                                    </h3>
                                    <p className="text-base font-semibold text-gray-900">
                                        Brand: { cat.brand }
                                    </p>
                                </div>
                            )
                        })
                    }
                </div>
            </div>
        </div>
    );
}

export default Home;
