const Categories = (props) => {

    const [categories, setCategories] = useState([]);
    
    const groupCategory = (category) => {
        var arr2 = arr.reduce( (a,b) => {
            var i = a.findIndex( x => x.id === b.id);
            return i === -1 ? a.push({ id : b.id, times : 1 }) : a[i].times++, a;
        }, []);
        
        setCategories(arr2);
    }

    return (
        <div class="bg-gray-100">
            <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
                <div class="max-w-2xl mx-auto py-16 sm:py-24 lg:py-32 lg:max-w-none">
                    <h2 class="text-2xl font-extrabold text-gray-900">Collections</h2>

                    <div class="mt-6 space-y-12 lg:space-y-0 lg:grid lg:grid-cols-3 lg:gap-x-6">
                        {
                            props.products.map((product, index) => {
                                <div class="group relative">
                                    <div class="relative w-full h-80 bg-white rounded-lg overflow-hidden group-hover:opacity-75 sm:aspect-w-2 sm:aspect-h-1 sm:h-64 lg:aspect-w-1 lg:aspect-h-1">
                                        <img src="https://tailwindui.com/img/ecommerce-images/home-page-02-edition-01.jpg" 
                                            alt="Desk with leather desk pad, walnut desk organizer, wireless keyboard and mouse, and porcelain mug." 
                                            class="w-full h-full object-center object-cover" />
                                    </div>
                                    <h3 class="mt-6 text-sm text-gray-500">
                                        <a href="#">
                                            <span class="absolute inset-0"></span>
                                            Desk and Office
                                        </a>
                                    </h3>
                                    <p class="text-base font-semibold text-gray-900">Work from home accessories</p>
                                </div>
                            })

                        }
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Categories;
